import './App.css';
import { useEffect, useState, Fragment } from 'react';

function App() {
  const [todoItems, setTodoItems] = useState(null);


  useEffect(() => {
    console.log(" Hey, I've loaded up");
    if (!todoItems) {
      fetch("http://localhost:8080/api/todoItems")
        .then((response) => response.json())
        .then((dataTodoItems) => {
          console.log(" Todo items list: ", dataTodoItems);
          setTodoItems(dataTodoItems);
        });
    }
  }, [todoItems]);
  return (
    <div>
      { todoItems ? todoItems.map((todoItem) => {
        const pendingStatus = todoItem.createdDate ? "Doing" : "Backlog";
        return (
          <Fragment key={todoItem.id}>
              <span> Status: {todoItem.done ? "Done" : pendingStatus} <br/></span>
              <span> Task:<br/>{todoItem.description} <br/></span> 
              <div class="date"> <p> Created date: {todoItem.createdDate} </p><br/></div>
              <span hidden={!todoItem.dueDate}> Due date: {todoItem.dueDate} <br/></span> 
              <span hidden={!todoItem.startDate}> Started date: {todoItem.startDate} <br/></span> 
              <span hidden={!todoItem.finishDate}> Finish date: {todoItem.finishDate} <br/></span> 
          </Fragment>
          );
      }) : "loading data..."}
    </div>
  );
}
export default App;
