import './App.css';
import { useEffect, useState, Fragment } from 'react';

function App() {
  const [todoItems, setTodoItems] = useState(null);


  useEffect(() => {
    console.log(" Hey, I've loaded up");
    if (!todoItems) {
      fetch("http://localhost:8080/api/todoItems")
        .then((response) => response.json())
        .then((dataTodoItems) => {
          console.log(" Todo items list: ", dataTodoItems);
          setTodoItems(dataTodoItems);
        });
    }
  }, [todoItems]);
  return (
    <div>
      { todoItems ? todoItems.map((todoItem) => {
        const pendingStatus = todoItem.createdDate ? "Doing" : "Backlog";
        return (
          <Fragment key={todoItem.id}>
            <flex> Status: {todoItem.done ? "Done" : pendingStatus} </flex>
            <flex> Task: {todoItem.description} </flex> 
            <span> Created date: {todoItem.createdDate} </span> 
            <span hidden={!todoItem.dueDate}> Due date: {todoItem.dueDate} </span> 
            <span hidden={!todoItem.startDate}> Started date: {todoItem.startDate} </span> 
            <span hidden={!todoItem.finishDate}> Finish date: {todoItem.finishDate} </span> 
          </Fragment>
          );
      }) : "loading data..."}
    </div>
  );
}
export default App;
