import { React, Fragment, useState } from "react";
import Moment from 'react-moment';

const TodoItem = (props) => {
    const todoItem = [todoItem, setTodoItem ] = useState(props.data);
    const pendingStatus = todoItem.createdDate ? "Doing" : "Backlog";
    return (
        <div>
            <Fragment>
                <span><p>Task: {todoItem.description}</p></span> 
                <span><p> Status: {todoItem.done ? "Done" : pendingStatus}</p></span>
                <span hidden={!todoItem.createdDate}> <p>Created date: <Moment date={todoItem.createdDate} format="DD/MM/YYYY"/> </p></span>
                <span hidden={!todoItem.dueDate}> <p>Due date: <Moment date={todoItem.dueDate} format="DD/MM/YYYY"/> </p> </span> 
                <span hidden={!todoItem.startDate}><p> Started date: <Moment date={todoItem.startDate} format="DD/MM/YYYY"/> </p> </span> 
                <span hidden={!todoItem.finishDate}><p>Finish date: <Moment date={todoItem.finishDate} format="DD/MM/YYYY"/> </p> </span> 
                <span hidden={!todoItem.finishDate}><p>Spend time: <Moment date={todoItem.finishDate - todoItem.startDate} format="hh:mm:ss"/> </p> </span> 
          </Fragment>
        </div>
    );
};

export default TodoItem;
